import React, { Component }  from 'react';
import {NavLink } from 'react-router-dom';

class Header extends Component {
	constructor(props) {
		super(props);
		this.State = {
			isActiveHome: true,
			isActiveAbout: false,
			isActiveContact: false,
			isActiveMenu: false
		}
	}
	Choose(NameActive) {
		switch (NameActive) {
			case "Home":
				this.setState = {
					isActiveHome: true,
					isActiveAbout: false,
					isActiveContact: false,
					isActiveMenu: false
				}
				return;
			case "Menu":
				this.setState = {
					isActiveHome: false,
					isActiveAbout: false,
					isActiveContact: false,
					isActiveMenu: true
				}
				return;
			case "Contact":
				this.setState = {
					isActiveHome: false,
					isActiveAbout: false,
					isActiveContact: true,
					isActiveMenu: false
				}
				return;
			case "About":
				this.setState = {
					isActiveHome: false,
					isActiveAbout: true,
					isActiveContact: false,
					isActiveMenu: false
				}
				return;
		}
	}
    render() {
        return(
        <header className="top-navbar">
		<nav className="navbar navbar-expand-lg navbar-light bg-light">
			<div className="container">
				<a className="navbar-brand" href="index.html">
					<img src="images/logo.png" alt="" />
				</a>
				<button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbars-rs-food" aria-controls="navbars-rs-food" aria-expanded="false" aria-label="Toggle navigation">
				  <span className="navbar-toggler-icon"></span>
				</button>
				<div className="collapse navbar-collapse" id="navbars-rs-food">
					<ul className="navbar-nav ml-auto">
								<li className='nav-item' id="home" onClick={this.Choose("Home")}><NavLink className="nav-link" to="/">Home</NavLink></li>
								<li className="nav-item" id="menuProduct" onClick={this.Choose("Menu")}><NavLink className="nav-link" to="/menu">Menu</NavLink></li>
						<li className="nav-item" id="about" onClick={this.Choose("About")}><NavLink className="nav-link" to="/about">About</NavLink></li>
						{/* <li className="nav-item dropdown">
							<a className="nav-link dropdown-toggle" href="#" id="dropdown-a" data-toggle="dropdown">Pages</a>
							<div className="dropdown-menu" aria-labelledby="dropdown-a">
								<a className="dropdown-item" href="reservation.html">Reservation</a>
								<a className="dropdown-item" href="stuff.html">Stuff</a>
								<a className="dropdown-item" href="gallery.html">Gallery</a>
							</div>
						</li> 
						<li className="nav-item dropdown">
							<a className="nav-link dropdown-toggle" href="#" id="dropdown-a" data-toggle="dropdown">Blog</a>
							<div className="dropdown-menu" aria-labelledby="dropdown-a">
								<a className="dropdown-item" href="blog.html">blog</a>
								<a className="dropdown-item" href="blog-details.html">blog Single</a>
							</div>
						</li>*/}
								<li className="nav-item" id="contact" onClick={this.Choose("Contact")}><NavLink className="nav-link" to="/contact">Contact</NavLink></li>
					</ul>
				</div>
			</div>
		</nav>
	</header>
        )
    }
        
}

export default Header;
