import axios from 'axios';
import * as Config from '../contraints/config';

export default function callApi(endpoint, method = 'POST', body) {
    console.log(body, 'body');
    alert(Config.API_URL + endpoint);
    return axios({
        method : method,
        //url : `${Config.API_URL}/api/${endpoint}`,
        url: 'https://localhost:44370//api/checkUser}',
        data : body,
    }).catch(err => {
        console.log(err);
    })
}
