import * as types from '../contraints/index';

var initState = {
	isActiveHome: true,
	isActiveMenu: false,
	isActiveAbout: false,
	isActiveContact: false
}

const chooseMenu = (state = initState, action) => {
    switch (action.type) {
        case types.CHOOSE_MENU_ABOUT:
            state = {
                isActiveHome: false,
                isActiveMenu: false,
                isActiveAbout: true,
                isActiveContact: false
            }
            return [...state]
        case types.CHOOSE_MENU_CONTACT:
            state = {
                isActiveHome: false,
                isActiveMenu: false,
                isActiveAbout: false,
                isActiveContact: true
            }
            return [...state]
        case types.CHOOSE_MENU_MENU:
            state = {
                isActiveHome: false,
                isActiveMenu: true,
                isActiveAbout: false,
                isActiveContact: false
            }
            return [...state]
        case types.CHOOSE_MENU_HOME:
            state = {
                isActiveHome: true,
                isActiveMenu: false,
                isActiveAbout: false,
                isActiveContact: false
            }
            return [...state]
        default:
            return [...state]
    }

}

export default chooseMenu;
