import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Product extends Component {
    render() {
        return (
            <tr>
                <th scope="row">{this.props.stt}</th>
                <td>{this.props.product.productName}</td>
                <td><image src={"image/" + this.props.product.productImage}/></td>
                <td>{this.props.product.productUnit}</td>
                <td>{this.props.product.productDescriptions}</td>
                <td>{this.props.product.productPrice}</td>
                <td className="text-center"><Link to="#"><i className="fa fa-pencil-square-o fa-2x" aria-hidden="true"></i></Link>|<i className="fa fa-trash-o fa-2x" aria-hidden="true"></i></td>

            </tr>
        )
    }
}

export default Product;
