import * as types from '../contraints/index';

export const chooseMenu = (menu) => {
    return() => {
        switch(menu) {
            case "Home":
                return { types: types.CHOOSE_MENU_HOME }
            case "Menu":
                return { types: types.CHOOSE_MENU_MENU }
            case "Contact":
                return { types: types.CHOOSE_MENU_CONTACT }
            case "About":
                return { types: types.CHOOSE_MENU_ABOUT }
        }
    }
}
