import React, { Component } from 'react';
import HeaderAdmin from '../Template/HeaderAdmin';
import NavBar from '../Template/NavBar';

class ProductList extends Component {
    render() {
        return (
            <div className="container-scroller">

                <HeaderAdmin />
                <div className="col-lg-12 col-12">
                    <div className="container-fluid page-body-wrapper">
                        <NavBar />
                        
                        <table className="table table-striped col-lg-12 col-12">
                            <thead>
                                <tr>
                                    <th scope="col">STT</th>
                                    <th scope="col">Tên món</th>
                                    <th scope="col">Giá món</th>
                                    <th scope="col">Ảnh</th>
                                    <th scope="col">Đơn vị</th>
                                    <th scope="col">Mô tả</th>
                                    <th scope="col">Thao tác</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}
var mapStateToProps = state => {
    return {
        products: state.products,
        categories: state.categories
    }
}

var mapDispathToProps = (dispatch, props) => {
    return {
        fetchProduct: (id) => {
            dispatch(actFetchProductCategoryRequest(id));
        },
        fetchCategory: () => {
            dispatch(actFetchCategoryRequest());
        }
    }
}

export default connect(mapStateToProps, mapDispathToProps)(ProductList);
